    showModelFit = FALSE,
    addcred = FALSE,
    addfit = TRUE,
    showweights = FALSE,
    steps = 5,
    xAxisTitle,
    pchForest = "15",
    forestOrder = "fit",
    fsntype = "Rosenthal",
    yaxis = "sei",
    yaxisInv = FALSE,
    enhanceFunnel = FALSE,
    lowerTOST = -0.5,
    upperTOST = 0.5,
    alphaTOST = 0.05,
    showTestTOST = TRUE,
    showInfPlot = FALSE) {

    if ( ! requireNamespace('jmvcore'))
        stop('MetaCorr requires jmvcore to be installed (restart may be required)')

    if ( ! missing(rcor)) rcor <- jmvcore::resolveQuo(jmvcore::enquo(rcor))
    if ( ! missing(samplesize)) samplesize <- jmvcore::resolveQuo(jmvcore::enquo(samplesize))
    if ( ! missing(slab)) slab <- jmvcore::resolveQuo(jmvcore::enquo(slab))
    if ( ! missing(moderatorcor)) moderatorcor <- jmvcore::resolveQuo(jmvcore::enquo(moderatorcor))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(rcor), rcor, NULL),
            `if`( ! missing(samplesize), samplesize, NULL),
            `if`( ! missing(slab), slab, NULL),
            `if`( ! missing(moderatorcor), moderatorcor, NULL))


    options <- MetaCorrOptions$new(
        rcor = rcor,
        samplesize = samplesize,
        slab = slab,
        moderatorcor = moderatorcor,
        methodmetacor = methodmetacor,
        cormeasure = cormeasure,
        moderatorType = moderatorType,
        level = level,
        showModelFit = showModelFit,
        addcred = addcred,
        addfit = addfit,
        showweights = showweights,
        steps = steps,
        xAxisTitle = xAxisTitle,
        pchForest = pchForest,
        forestOrder = forestOrder,
        fsntype = fsntype,
        yaxis = yaxis,
        yaxisInv = yaxisInv,
        enhanceFunnel = enhanceFunnel,
        lowerTOST = lowerTOST,
        upperTOST = upperTOST,
        alphaTOST = alphaTOST,
        showTestTOST = showTestTOST,
        showInfPlot = showInfPlot)

    analysis <- MetaCorrClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}
